import React, { useState, useEffect } from 'react';

const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    // Fetch questions from your API or MongoDB here
    // Example:
    // fetch('/api/questions')
    //   .then(response => response.json())
    //   .then(data => setQuestions(data));

    // For now, let's use static data
    setQuestions([
      { id: 1, topic: 'Chemistry', question: 'What is H2O?', options: ['Water', 'Oxygen', 'Hydrogen', 'Carbon'], answer: 'Water' },
      { id: 2, topic: 'Math', question: 'What is 2 + 2?', options: ['3', '4', '5', '6'], answer: '4' },
    ]);
  }, []);

  const handleDelete = (id) => {
    // Logic to delete question (e.g., via API call)
    console.log(`Deleting question with ID: ${id}`);
    setQuestions(questions.filter(q => q.id !== id));
  };

  return (
    <div>
      <h2>Manage Questions</h2>
      <ul>
        {questions.map((q) => (
          <li key={q.id}>
            <p>Topic: {q.topic}</p>
            <p>Question: {q.question}</p>
            <p>Options: {q.options.join(', ')}</p>
            <p>Answer: {q.answer}</p>
            <button onClick={() => handleDelete(q.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageQuestions;
