import React, { useState } from 'react';

const AddQuestion = () => {
  const [topic, setTopic] = useState('');
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState({ option1: '', option2: '', option3: '', option4: '' });
  const [answer, setAnswer] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic to handle form submission, save question to database
    console.log('Question added:', { topic, question, options, answer });
  };

  return (
    <div>
      <h2>Add a Question</h2>
      <form onSubmit={handleSubmit}>
        <label>Topic:</label>
        <input type="text" value={topic} onChange={(e) => setTopic(e.target.value)} />
        <br />
        <label>Question:</label>
        <input type="text" value={question} onChange={(e) => setQuestion(e.target.value)} />
        <br />
        <label>Option 1:</label>
        <input type="text" value={options.option1} onChange={(e) => setOptions({ ...options, option1: e.target.value })} />
        <br />
        <label>Option 2:</label>
        <input type="text" value={options.option2} onChange={(e) => setOptions({ ...options, option2: e.target.value })} />
        <br />
        <label>Option 3:</label>
        <input type="text" value={options.option3} onChange={(e) => setOptions({ ...options, option3: e.target.value })} />
        <br />
        <label>Option 4:</label>
        <input type="text" value={options.option4} onChange={(e) => setOptions({ ...options, option4: e.target.value })} />
        <br />
        <label>Answer:</label>
        <input type="text" value={answer} onChange={(e) => setAnswer(e.target.value)} />
        <br />
        <button type="submit">Add Question</button>
      </form>
    </div>
  );
};

export default AddQuestion;
