import React, { useState } from "react";
import axios from "axios";

const AddQuestion = () => {
    const [formData, setFormData] = useState({
        category: "",
        question: "",
        options: ["", "", "", ""],
        answer: "",
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleOptionChange = (index, value) => {
        const updatedOptions = [...formData.options];
        updatedOptions[index] = value;
        setFormData({ ...formData, options: updatedOptions });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:5000/api/questions/add", formData);
            alert("Question added successfully!");
        } catch (err) {
            console.error("Error adding question", err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Add Question</h2>
            <div>
                <label>Category:</label>
                <input type="text" name="category" value={formData.category} onChange={handleChange} required />
            </div>
            <div>
                <label>Question:</label>
                <input type="text" name="question" value={formData.question} onChange={handleChange} required />
            </div>
            <div>
                <label>Options:</label>
                {formData.options.map((option, index) => (
                    <input
                        key={index}
                        type="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        required
                    />
                ))}
            </div>
            <div>
                <label>Answer:</label>
                <input type="text" name="answer" value={formData.answer} onChange={handleChange} required />
            </div>
            <button type="submit">Add Question</button>
        </form>
    );
};

export default AddQuestion;
