import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const QuestionList = () => {
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/questions");
                setQuestions(response.data);
            } catch (err) {
                console.error("Error fetching questions", err);
            }
        };
        fetchQuestions();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/questions/delete/${id}`);
            setQuestions(questions.filter((question) => question._id !== id));
        } catch (err) {
            console.error("Error deleting question", err);
        }
    };

    return (
        <div>
            <h2>Question List</h2>
            <ul>
                {questions.map((question) => (
                    <li key={question._id}>
                        <strong>{question.category}</strong>: {question.question}
                        <Link to={`/edit/${question._id}`}>Edit</Link>
                        <button onClick={() => handleDelete(question._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default QuestionList;
