import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const EditQuestion = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        category: "",
        question: "",
        options: ["", "", "", ""],
        answer: "",
    });

    useEffect(() => {
        const fetchQuestion = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/questions`);
                const question = response.data.find((q) => q._id === id);
                setFormData(question);
            } catch (err) {
                console.error("Error fetching question", err);
            }
        };
        fetchQuestion();
    }, [id]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleOptionChange = (index, value) => {
        const updatedOptions = [...formData.options];
        updatedOptions[index] = value;
        setFormData({ ...formData, options: updatedOptions });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5000/api/questions/update/${id}`, formData);
            alert("Question updated successfully!");
            navigate("/");
        } catch (err) {
            console.error("Error updating question", err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Question</h2>
            <div>
                <label>Category:</label>
                <input type="text" name="category" value={formData.category} onChange={handleChange} required />
            </div>
            <div>
                <label>Question:</label>
                <input type="text" name="question" value={formData.question} onChange={handleChange} required />
            </div>
            <div>
                <label>Options:</label>
                {formData.options.map((option, index) => (
                    <input
                        key={index}
                        type="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        required
                    />
                ))}
            </div>
            <div>
                <label>Answer:</label>
                <input type="text" name="answer" value={formData.answer} onChange={handleChange} required />
            </div>
            <button type="submit">Update Question</button>
        </form>
    );
};

export default EditQuestion;
